import React from 'react'
import './Experiences.css'


class Experiences extends React.Component {
    constructor(props) {
    super(props);
    this.state = {
     locations: '',
     addTrips: ''
    }
    }

    handleChange = e => {
        const {name , value } = e.target
        this.setState({ [name]:value })
    }

    // locationHandler = e => {
    //     this.setState( {locations:e.target.value} )
    // }

    // tripHandler = e => {
    //     this.setState( {addTrips:e.target.value} )
    // }

    render() {
        return(
            <div className='profile-app'>

            <div className='nav-section'>
                <div className='exp-title'>
                    <h3 className='exp-title-text'><span>WanderLust</span> ...Traveling made easy</h3>
                </div>

                <div className='profile-link'>
                    <a href='#'>Profile</a>
                </div>
            </div> {/* nav-section end */}


            <div className='top-section'>

                 <div className='user-info'>
                    <h3>Name:</h3>
                    <h3>E-mail:</h3>
                    <h3>Location:</h3>
                  </div>

                  <form className='form-location'>
                      <div>
                        <button onSubmit><i className="fas fa-plus"></i></button>
                      </div>

                      <div className='location-section'>
                        <input
                        className='locations'
                        placeholder='Locations'
                        name='location'
                        value={this.state.name}
                        onChange={this.handleChange}>
                        </input>
                      </div>
                  </form> {/* form-location end */}

                  <form className='form-trip'>
                      <div>
                         <button><i className="fas fa-plus"></i></button>
                      </div>

                      <div className='trips-section'>
                        <input
                        className='trips'
                        placeholder='Add Trips'
                        name='trip'
                        value={this.state.name}
                        onChange={this.handleChange}>
                        </input>
                      </div>
                  </form> {/* form-trip end */}

            </div> {/* top-section end */}

            </div> /* profile-app end */
        )
    }
}
export default Experiences
