import React from 'react'
import './Signup.css'
import { Link } from 'react-router-dom'
import axios from 'axios'

class SignUp extends React.Component {
  constructor() {
  super();
  this.state = {
  name: '',
  username: '',
  password: '',
  userType: ''
  }
  }

//   componentDidMount() {
