import './Experiences.css'


class Experiences extends React.Component {
    constructor(props) {
    super(props);
    this.state = {
     locations: '',
     addTrips: ''
    }
    }

    handleChange = e => {
        const {name , value } = e.target
        this.setState({ [name]:value })
    }

    handleSubmit = e => {
        e.preventDefault();
        console.log('hi')
        console.log(this.state)
    }

