                </div>

                <div className='profile-link'>
                    <a href='#'>Profile</a>
                </div>
            </div> {/* nav-section end */}


            <div className='top-section'>

                 <div className='user-info'>
                    <h3>Name:</h3>
                    <h3>Username:</h3>
                    <h3>E-mail:</h3>
                  </div>

                 <div className='saved-inputs'>
                    <h3>Locations:</h3>
                    <h3>Trips Made:</h3>
                  </div>

            </div> {/* top-section end */}

            </div> /* profile-app end */
        )
    }
}
export default Experiences
